package a04_statedment;

public class HW_exp4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 4) 이중 for 문을 이요하여 방정식 4x+5y=60인 해를 구하시오
		// x,y는 10 이하의 정수 =>>   출력형식 x:@@, y:@@ 
		 
		for(int x=1; x<=10; x++){
			for(int y=1; y<=10; y++){
				if(((4*x)+(5*y))==60){
						System.out.println("x 값 "+x+" , "+"y 값 "+y);
						System.out.println("4 * "+x+" + "+"5 * "+y+" =  60");
				}
			}
		}
		
	}

}
