package javaexp.a06_Object;

class Person00{
	String name;
	String address;
	int age;
	
	Person00(String name){
		this.name=name;
	}
	
	void inputData(String address, int age){
		this.address=address;
		this.age=age;		
	}
	
	void introMyself(){
		System.out.println("My name is "+name);
		System.out.println("I am "+age+" years old");
		System.out.println("I live in "+address);
	}
}

public class A20170419_hw2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Person00 p1 = new Person00("choi");
		p1.inputData("GANGNAM, SEOUL", 29);
		p1.introMyself();
	}

}
