package javaexp.a02_variable;

public class A01_declare_allot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 자바에서 변수 선언;
		// type 식별자
		int age;
		age = 25;// 데이터 할당..
		// 선언 + 할당(저장)
		double weight = 65.7;
		// 변수값 읽어서, 새로운 데이터에 할당.
		int after10Year=age+10;
		// 데이터 형태에 맞는 데이터를 할당해야 에러가 발생하지 않는다.
		// 데이터 형태에 맞지 않는 할당..
//		age = 25.7; 정수형 type에 실수형의 데이터를 할당시 에러발생..
//		age ="나이25세"; 정수형 type에 문자형의 데이터를 할당시 에러발생..
		System.out.println("나이는 "+age+"살이며,");
		System.out.println("몸무게는 "+weight+"kg입니다!!");
		
		
	}

}
