package javaexp.a10_api;

import java.io.IOException;

class Person{}
public class A01_lang_package {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 자바 모든 클래스의 최상위 클래스
		Object o01 = new Object();
		// java.lang.Object@15db9742 : 클래스명@주소값
		System.out.println("Object의 toString():"+o01.toString());
		Person p = new Person();
//		javaexp.a10_api.Person@6d06d69c
		System.out.println("Person의 toString():"+p.toString());
		
//		System.in : 입력 관련 객체.
//		read()를 통해 입력 처리
		try {
			System.out.println("문자 한자입력:"+(char)System.in.read());
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
//		System.out : 출력 관련 객체.
//		println()를 통해서 출력 처리
		System.err.println("에러 로그 처리 err.println()");
		
		
	}

}
