package javahomework.a04_KimDongBeom;

class SelfIntro {
	String name;
	String loca;
	int age;
	
	SelfIntro(String name){
		this.name = name;
	}
	void inputData(String loca, int age){
		this.loca = loca;
		this.age = age;
	}
	
	void introMyself(){
		System.out.println("내 이름은 "+name+"이고, 나이는 "+age+"이며, 사는 곳은 "+loca+"입니다.");
	}
}

public class A0419_Introduce {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		SelfIntro a1 = new SelfIntro("ABC");
		a1.inputData("Seoul", 26);
		a1.introMyself();
	}

}
