package javahomework.a18_yoonyoungju;
/*
자기소개서 ( 1단계)
	field : 이름, 사는곳, 나이
		생성자 (이름 입력)
	inputData(사는곳, 나이)
	introMyself(): 출력 처리 
		내이름은 @@@이고, 
나이는 @@이며, 사는 곳은 @@@ 입니다 .
 */
class Myinfo{
	String name;
	String place;
	int age;
	Myinfo(String name){
		this.name=name;
	}
	void inputData(String place, int age){
		this.place = place;
		this.age = age;
	}	
	void introMyself(){
		System.out.print("내이름은"+name+"이고, 나이는"+age+"이며, 사는 곳은"+place+"입니다 .");
	}
}
public class Method_exp_lv1 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Myinfo m = new Myinfo("윤영주");
		m.inputData("인천", 26);
		m.introMyself();
	}
}


