package javahomework.a23_Janghyeyeong.a170419.Step1;

class Intro{
	String name;
	String loc;
	int age;
	
	Intro(String name){
		this.name=name;
	}
	
	void inputData(String loc, int age){
		this.loc=loc;
		this.age=age;
		return;
	}
	
	void introMyself(){
		System.out.println(" 내 이름은 "+name+"이고, 나이는 "
				+age+"이며, 사는 곳은 "+loc+"입니다.");
	}
	
}

public class introduceExp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Intro i1 = new Intro("홍길동");
		
		i1.inputData("강남", 24);
		i1.introMyself();
		

	}

}
